    /*
     * Get user by id_user
     */
    public function get_user($id_user)
    {
        return $this->db->get_where('user', array('id_user'=>$id_user))->row_array();
    }

    public function get_all_nonteknisi()
    {
        $this->db->select('us.id_user, us.username');
        $this->db->from('user us');
        $this->db->join('notifpengajjuan n', 'us.id_user = n.id_notif', 'left');
        $this->db->where('n.id_user is null');
        $this->db->where(' us.id_user != 1');
    }

