    /*
     * Get all user
     */
    public function get_all_user()
    {
        $this->db->order_by('id_user', 'desc');
        return $this->db->get('user')->result_array();
    }
    public function get_user_exadmin()
    {
        $this->db->order_by('id_user', 'desc');
        $this->db->where("id_user != 1");
        return $this->db->get('user')->result_array();
    }
